import React, { useState, useEffect } from "react";
import "./AttendanceTracker.css";

const AttendanceTracker = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  // Fetch data from backend
  useEffect(() => {
    fetch("/api/students") // Replace with your backend URL
      .then(res => res.json())
      .then(data => {
        setStudents(data);
        setLoading(false);
      })
      .catch(err => {
        console.error("Error fetching students:", err);
        setLoading(false);
      });
  }, []);

  // Update locally and backend
  const updateStudent = (id, key, value) => {
    // Update frontend state
    setStudents(students.map(s =>
      s.id === id ? { ...s, [key]: value } : s
    ));

    // Send update to backend
    fetch(`/api/students/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ [key]: value })
    }).catch(err => console.error("Error updating student:", err));
  };

  if (loading) return <p>Loading students...</p>;

  return (
    <div className="attendance-page">
      <h1>ðŸ“Š Attendance & Grade Tracker</h1>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Attendance (%)</th>
            <th>Grade</th>
          </tr>
        </thead>

        <tbody>
          {students.map(student => (
            <tr key={student.id}>
              <td>{student.name}</td>

              <td>
                <input
                  type="number"
                  value={student.attendance}
                  onChange={(e) =>
                    updateStudent(student.id, "attendance", e.target.value)
                  }
                />
              </td>

              <td>
                <select
                  value={student.grade}
                  onChange={(e) =>
                    updateStudent(student.id, "grade", e.target.value)
                  }
                >
                  <option>A</option>
                  <option>B</option>
                  <option>C</option>
                  <option>D</option>
                </select>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AttendanceTracker;
