import React, { useEffect, useState } from "react";
import axios from "axios";
import { FaEdit, FaEye, FaTrashAlt } from "react-icons/fa";
import { Link } from "react-router-dom";
import Search from "../common/Search";

const StudentsView = () => {
  const [students, setStudents] = useState([]);
  const [search, setSearch] = useState("");

  useEffect(() => {
    loadStudents();
  }, []);

  const loadStudents = async () => {
    try {
      const result = await axios.get("http://localhost:8080/students", {
        validateStatus: () => true,
      });

      if (result.status === 200 || result.status === 302) {
        setStudents(result.data);
      }
    } catch (err) {
      console.error(err);
    }
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:8080/students/delete/${id}`);
      loadStudents();
    } catch (err) {
      console.error(err);
    }
  };

  // Filter students safely
  const filteredStudents = students.filter(
    (st) =>
      st.firstName?.toLowerCase().includes(search.toLowerCase()) || "" // optional chaining
  );

  return (
    <section>
      <Search search={search} setSearch={setSearch} />
      <table className="table table-bordered table-hover shadow">
        <thead>
          <tr className="text-center">
            <th>ID</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Email</th>
            <th>Department</th>
            <th colSpan="3">Actions</th>
          </tr>
        </thead>

        <tbody className="text-center">
          {filteredStudents.map((student, index) => (
            <tr key={student.id}>
              <th scope="row">{index + 1}</th>
              <td>{student.firstName || "-"}</td>
              <td>{student.lastName || "-"}</td>
              <td>{student.email || "-"}</td>
              <td>{student.department || "-"}</td>
              <td className="mx-2">
                <Link to={`/student-profile/${student.id}`} className="btn btn-info">
                  <FaEye />
                </Link>
              </td>
              <td className="mx-2">
                <Link to={`/edit-student/${student.id}`} className="btn btn-warning">
                  <FaEdit />
                </Link>
              </td>
              <td className="mx-2">
                <button
                  className="btn btn-danger"
                  onClick={() => handleDelete(student.id)}
                >
                  <FaTrashAlt />
                </button>
              </td>
            </tr>
          ))}

          {filteredStudents.length === 0 && (
            <tr>
              <td colSpan="8" className="text-center">
                No students found.
              </td>
            </tr>
          )}
        </tbody>
      </table>
    </section>
  );
};

export default StudentsView;
