import React from "react";

const About = () => {
	return (
		<div className="container mt-5">
			<h2>About Student Management System</h2>
			<p>
				This system helps institutions manage student records,
				attendance, grades, reports, and communication efficiently.
			</p>
		</div>
	);
};

export default About;
