import React, { useState } from "react";
import "./AttendanceTracker.css";

const AttendanceTracker = () => {
	const [students, setStudents] = useState([
		{ id: 1, name: "Aarav", attendance: 90, grade: "A" },
		{ id: 2, name: "Diya", attendance: 82, grade: "B" },
		{ id: 3, name: "Kiran", attendance: 75, grade: "C" }
	]);

	const updateAttendance = (id, value) => {
		setStudents(students.map(s =>
			s.id === id ? { ...s, attendance: value } : s
		));
	};

	const updateGrade = (id, value) => {
		setStudents(students.map(s =>
			s.id === id ? { ...s, grade: value } : s
		));
	};

	return (
		<div className="attendance-page">
			<h1>ðŸ“Š Attendance & Grade Tracker</h1>

			<table>
				<thead>
					<tr>
						<th>Name</th>
						<th>Attendance (%)</th>
						<th>Grade</th>
					</tr>
				</thead>

				<tbody>
					{students.map(student => (
						<tr key={student.id}>
							<td>{student.name}</td>

							<td>
								<input
									type="number"
									value={student.attendance}
									onChange={(e) =>
										updateAttendance(student.id, e.target.value)
									}
								/>
							</td>

							<td>
								<select
									value={student.grade}
									onChange={(e) =>
										updateGrade(student.id, e.target.value)
									}
								>
									<option>A</option>
									<option>B</option>
									<option>C</option>
									<option>D</option>
								</select>
							</td>
						</tr>
					))}
				</tbody>
			</table>
		</div>
	);
};

export default AttendanceTracker;
