import React, { useState, useEffect } from "react";
import "./AttendanceTracker.css";

const AttendanceTracker = () => {
  const [students, setStudents] = useState([]);
  const [loading, setLoading] = useState(true);

  // 1Ô∏è‚É£ Fetch students from backend
  useEffect(() => {
    fetch("http://localhost:8080/students") // Spring Boot backend
      .then(res => res.json())
      .then(data => {
        setStudents(data);
        setLoading(false);
      })
      .catch(err => console.error("Error fetching students:", err));
  }, []);

  // 2Ô∏è‚É£ Update student (attendance or grade)
  const updateStudent = (id, key, value) => {
    // Optimistically update UI
    setStudents(students.map(s => (s.id === id ? { ...s, [key]: value } : s)));

    // Send PUT request to backend
    const student = students.find(s => s.id === id);
    fetch(`http://localhost:8080/students/update/${id}`, {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ ...student, [key]: value })
    }).catch(err => console.error("Error updating student:", err));
  };

  // 3Ô∏è‚É£ Add new student
  const addStudent = () => {
    const newStudent = { name: "New Student", attendance: 0, grade: "A" };
    fetch("http://localhost:8080/students", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newStudent)
    })
      .then(res => res.json())
      .then(student => setStudents([...students, student]))
      .catch(err => console.error("Error adding student:", err));
  };

  // 4Ô∏è‚É£ Delete student
  const deleteStudent = (id) => {
    fetch(`http://localhost:8080/students/delete/${id}`, { method: "DELETE" })
      .then(() => setStudents(students.filter(s => s.id !== id)))
      .catch(err => console.error("Error deleting student:", err));
  };

  if (loading) return <p>Loading students...</p>;

  return (
    <div className="attendance-page">
      <h1>üìä Attendance & Grade Tracker</h1>
      <button onClick={addStudent}>‚ûï Add Student</button>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Attendance (%)</th>
            <th>Grade</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {students.map(student => (
            <tr key={student.id}>
              <td>{student.name}</td>

              <td>
                <input
                  type="number"
                  value={student.attendance}
                  onChange={e => updateStudent(student.id, "attendance", e.target.value)}
                />
              </td>

              <td>
                <select
                  value={student.grade}
                  onChange={e => updateStudent(student.id, "grade", e.target.value)}
                >
                  <option>A</option>
                  <option>B</option>
                  <option>C</option>
                  <option>D</option>
                </select>
              </td>

              <td>
                <button onClick={() => deleteStudent(student.id)}>üóëÔ∏è Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AttendanceTracker;
